import React, { useState, useEffect } from "react";
import axios from "../services/api";

const CommunicationMethod = () => {
  const [methods, setMethods] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    sequence: "",
    mandatory: false,
  });

  useEffect(() => {
    fetchMethods();
  }, []);

  const fetchMethods = async () => {
    const response = await axios.get("/admin/communication-methods");
    setMethods(response.data);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("/admin/communication-methods", formData);
    fetchMethods();
    alert("Method added!");
  };

  return (
    <div>
      <h1>Communication Methods</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          onChange={handleInputChange}
        />
        <textarea
          name="description"
          placeholder="Description"
          onChange={handleInputChange}
        ></textarea>
        <input
          type="number"
          name="sequence"
          placeholder="Sequence"
          onChange={handleInputChange}
        />
        <label>
          Mandatory:
          <input
            type="checkbox"
            name="mandatory"
            onChange={(e) =>
              setFormData({ ...formData, mandatory: e.target.checked })
            }
          />
        </label>
        <button type="submit">Add Method</button>
      </form>
      <h2>Methods</h2>
      <ul>
        {methods.map((method) => (
          <li key={method.id}>{method.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default CommunicationMethod;
