import React, { useState, useEffect } from "react";
import axios from "../services/api";
import { Line } from "react-chartjs-2";

const OverdueTrends = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get("/reports/overdue-trends")
      .then((response) => setData(response.data))
      .catch((error) => console.error("Error fetching overdue trends:", error));
  }, []);

  const chartData = {
    labels: data.map((item) => item.month),
    datasets: [
      {
        label: "Overdue Communications",
        data: data.map((item) => item.overdue_count),
        fill: false,
        backgroundColor: "rgba(255,99,132,0.2)",
        borderColor: "rgba(255,99,132,1)",
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="overdue-trends">
      <h1>Overdue Communication Trends</h1>
      <Line data={chartData} />
    </div>
  );
};

export default OverdueTrends;
