import React, { useState, useEffect } from "react";
import axios from "../services/api";

const Dashboard = () => {
  const [dashboardData, setDashboardData] = useState([]);

  useEffect(() => {
    axios
      .get("user/dashboard")
      .then((response) => setDashboardData(response.data))
      .catch((error) => console.error("Error fetching dashboard data:", error));
  }, []);

  return (
    <div className="dashboard">
      <h1>Communication Dashboard</h1>
      <table>
        <thead>
          <tr>
            <th>Company Name</th>
            <th>Last 5 Communications</th>
            <th>Next Scheduled Communication</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {dashboardData.map((company, index) => (
            <tr key={index} className={company.highlight}>
              <td>{company.company_name}</td>
              <td>
                {company.last_five_communications.map((comm, i) => (
                  <div key={i}>
                    {comm.type} - {comm.date}
                  </div>
                ))}
              </td>
              <td>{company.next_scheduled_communication}</td>
              <td>
                {company.highlight === "red"
                  ? "Overdue"
                  : company.highlight === "yellow"
                    ? "Due Today"
                    : "On Schedule"}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
