import React, { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import api from "../../services/api";
import "./Reports.css";

const FrequencyReport = () => {
  const [chartData, setChartData] = useState(null);
  const [filter, setFilter] = useState({
    companyId: "",
    startDate: "",
    endDate: "",
  });

  useEffect(() => {
    fetchFrequencyData();
  }, []);

  const fetchFrequencyData = async () => {
    try {
      const response = await api.get("/api/reports/communication-frequency", {
        params: filter,
      });
      const data = response.data;

      setChartData({
        labels: data.methods,
        datasets: [
          {
            label: "Communication Frequency",
            data: data.frequencies,
            backgroundColor: ["#33ffff", "#66ffff", "#99ffff", "#00ffff"],
          },
        ],
      });
    } catch (error) {
      console.error("Error fetching frequency report:", error);
    }
  };

  const handleFilterChange = (e) => {
    setFilter({ ...filter, [e.target.name]: e.target.value });
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
    fetchFrequencyData();
  };

  return (
    <div>
      <h1>Communication Frequency Report</h1>
      <form onSubmit={handleFilterSubmit} className="filter-form">
        <label>
          Company ID:
          <input
            type="text"
            name="companyId"
            value={filter.companyId}
            onChange={handleFilterChange}
          />
        </label>
        <label>
          Start Date:
          <input
            type="date"
            name="startDate"
            value={filter.startDate}
            onChange={handleFilterChange}
          />
        </label>
        <label>
          End Date:
          <input
            type="date"
            name="endDate"
            value={filter.endDate}
            onChange={handleFilterChange}
          />
        </label>
        <button type="submit">Apply Filters</button>
      </form>
      {chartData && <Bar data={chartData} />}
    </div>
  );
};

export default FrequencyReport;
