import React, { useState, useEffect } from "react";
import api from "../../services/api";
import "./Reports.css";

const ActivityLog = () => {
  const [logs, setLogs] = useState([]);

  useEffect(() => {
    fetchActivityLog();
  }, []);

  const fetchActivityLog = async () => {
    try {
      const response = await api.get("/api/reports/activity-log");
      setLogs(response.data);
    } catch (error) {
      console.error("Error fetching activity log:", error);
    }
  };

  return (
    <div>
      <h1>Real-Time Activity Log</h1>
      <table className="log-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Company</th>
            <th>Method</th>
            <th>Notes</th>
          </tr>
        </thead>
        <tbody>
          {logs.map((log, index) => (
            <tr key={index}>
              <td>{log.date}</td>
              <td>{log.company}</td>
              <td>{log.method}</td>
              <td>{log.notes}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ActivityLog;
