import React, { useState, useEffect } from "react";
import api from "../../services/api";
import { saveAs } from "file-saver";
import "./Reports.css";

const ActivityLog = () => {
  const [logs, setLogs] = useState([]);
  const [filters, setFilters] = useState({
    startDate: "",
    endDate: "",
    companyId: "",
  });

  useEffect(() => {
    fetchActivityLog();
  }, []);

  const fetchActivityLog = async () => {
    try {
      const response = await api.get("/reports/activity-log", {
        params: filters,
      });
      setLogs(response.data);
    } catch (error) {
      console.error("Error fetching activity log:", error);
    }
  };

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
    fetchActivityLog();
  };

  const handleExport = async (format) => {
    try {
      const response = await api.get(`/reports/activity-log/export`, {
        params: { format },
        responseType: "blob", // Important for file downloads
      });
      const fileName = `activity_log.${format}`;
      saveAs(response.data, fileName); // Save file
    } catch (error) {
      console.error(`Error exporting activity log as ${format}:`, error);
    }
  };

  return (
    <div>
      <h1>Real-Time Activity Log</h1>
      <form onSubmit={handleFilterSubmit} className="filter-form">
        <label>
          Start Date:
          <input
            type="date"
            name="startDate"
            value={filters.startDate}
            onChange={handleFilterChange}
          />
        </label>
        <label>
          End Date:
          <input
            type="date"
            name="endDate"
            value={filters.endDate}
            onChange={handleFilterChange}
          />
        </label>
        <label>
          Company ID:
          <input
            type="text"
            name="companyId"
            value={filters.companyId}
            onChange={handleFilterChange}
          />
        </label>
        <button type="submit">Apply Filters</button>
      </form>

      {/* Export Buttons */}
      <div className="export-buttons">
        <button onClick={() => handleExport("csv")}>Export as CSV</button>
        <button onClick={() => handleExport("pdf")}>Export as PDF</button>
      </div>

      <table className="log-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Company</th>
            <th>Method</th>
            <th>Notes</th>
          </tr>
        </thead>
        <tbody>
          {logs.map((log, index) => (
            <tr key={index}>
              <td>{log.date}</td>
              <td>{log.company}</td>
              <td>{log.method}</td>
              <td>{log.notes}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ActivityLog;
