import React, { useState } from 'react';
import Sidebar from "../Shared/Sidebar";
import { Outlet } from "react-router-dom";
import { Menu, X } from 'lucide-react';

const AdminLayout = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(true);
  
  const adminLinks = [
    { name: "Company List", path: "/admin/company-list" },
    { name: "Communication Method", path: "/admin/communication-method" },
  ];

  return (
    <div className="min-h-screen bg-[#030014] text-white">
      <div className="flex relative">
        {/* Mobile Menu Button */}
        <button 
          className="lg:hidden fixed top-4 left-4 z-50 p-2 bg-transparent"
          onClick={() => setSidebarOpen(!isSidebarOpen)}
        >
          {isSidebarOpen ? <X size={24} /> : <Menu size={24} />}
        </button>

        {/* Sidebar */}
        <div className={`
          fixed lg:static lg:translate-x-0 z-40
          transition-transform duration-300 ease-in-out
          ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'}
        `}>
          <div className="relative z-20 h-screen">
            <Sidebar links={adminLinks} title="Admin Dashboard" />
          </div>
        </div>

        {/* Main Content */}
        <main className="flex-1 p-6 lg:p-8">
          <div className="max-w-7xl mx-auto">
            <div className="backdrop-blur-lg bg-opacity-30 bg-black rounded-lg border border-[#00fff2] shadow-[0_0_15px_rgba(0,255,242,0.3)] p-6">
              <Outlet />
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default AdminLayout;
